}","usedDeprecatedRules":[]},{"filePath":"/Users/pro/Windventurefinal/scripts/visual-analysis.js","messages":[{"ruleId":null,"fatal":true,"severity":2,"message":"Parsing error: Unexpected keyword 'debugger'. (370:8)","line":370,"column":8,"nodeType":null}],"suppressedMessages":[],"errorCount":1,"fatalErrorCount":1,"warningCount":0,"fixableErrorCount":0,"fixableWarningCount":0,"source":"#!/usr/bin/env node

