  return {
    unit_price: unitPrice,
    unit_price_cents: unitPrice * 100,
    subtotal,
    subtotal_cents: subtotal * 100,
    tax_rate: taxRate,
    tax_amount: taxAmount,
    tax_amount_cents: Math.round(taxAmount * 100),
    total_price: total,
    total_price_cents: Math.round(total * 100)
  };
}

