                    </div>
                    <div className='flex justify-between'>
                      <span>Phone:</span>
                      <span className='font-semibold'>{formData.phone}</span>
                    </div>
                    <div className='flex justify-between'>
                      <span>Date:</span>
                      <span className='font-semibold'>{formData.date}</span>
                    </div>
                    <div className='flex justify-between'>
                      <span>Participants:</span>
                      <span className='font-semibold'>
                        {formData.participants}{' '}
                        {formData.participants === 1 ? 'person' : 'people'}
                      </span>
                    </div>

                    <div className='border-t pt-3 mt-4'>
                      <div className='flex justify-between text-lg font-bold'>
                        <span>Total Amount:</span>
                        <span className='text-blue-600'>
                          €{totalPrice.toLocaleString()}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Security Badge */}
                <div className='flex items-center justify-center gap-3 text-gray-600'>
                  <Shield className='w-5 h-5 text-green-600' />
                  <span className='text-sm'>
                    Secure payment with SSL encryption
                  </span>
                  <Star className='w-5 h-5 text-yellow-500' />
                </div>

                {/* Error Display */}
                {error && (
                  <motion.div
                    initial={{ opacity: 0, y: -10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className='bg-red-50 border border-red-200 rounded-xl p-4'
                  >
                    <div className='flex items-center gap-3 text-red-700'>
                      <AlertCircle className='w-5 h-5 flex-shrink-0' />
                      <p>{error}</p>
                    </div>
                  </motion.div>
                )}
              </motion.div>
            )}
          </AnimatePresence>

          {/* Navigation Buttons */}
          <div className='flex justify-between items-center mt-8 pt-6 border-t'>
            <motion.button
              type='button'
              onClick={prevStep}
              disabled={currentStep === 0}
              whileHover={currentStep > 0 ? { scale: 1.05 } : {}}
              whileTap={currentStep > 0 ? { scale: 0.95 } : {}}
              className={`px-6 py-3 rounded-xl font-semibold transition-all duration-200 ${
                currentStep === 0
                  ? 'text-gray-400 cursor-not-allowed'
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              Previous
            </motion.button>

            {currentStep < 2 ? (
              <motion.button
                type='button'
                onClick={nextStep}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className='px-8 py-3 bg-gradient-to-r from-blue-600 to-cyan-600 text-white rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all duration-200'
              >
                Continue
              </motion.button>
            ) : (
              <motion.button
                type='submit'
                disabled={isSubmitting}
                whileHover={!isSubmitting ? { scale: 1.05 } : {}}
                whileTap={!isSubmitting ? { scale: 0.95 } : {}}
                className='px-8 py-3 bg-gradient-to-r from-green-600 to-emerald-600 text-white rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2'
              >
                {isSubmitting ? (
                  <>
                    <Loader2 className='w-5 h-5 animate-spin' />
                    Processing...
                  </>
                ) : (
                  <>
                    <CreditCard className='w-5 h-5' />
                    Complete Booking
                  </>
                )}
              </motion.button>
            )}
          </div>
        </form>
      </motion.div>
    </div>
  );
}
