import React, { useState } from 'react';
import Image from 'next/image';

interface SafeImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  className?: string;
  priority?: boolean;
  fallbackSrc?: string;
  fallbackText?: string;
}

export default function SafeImage({
  src,
  alt,
  width = 400,
  height = 300,
  className = '',
  priority = false,
  fallbackSrc = '/images/dakhla/dakhla-lagoon-1.jpg',
  fallbackText = 'Dakhla Kitesurfing',
}: SafeImageProps) {
  const [imageError, setImageError] = useState(false);
  const [fallbackError, setFallbackError] = useState(false);

  const handleError = () => {
    if (!imageError) {
      setImageError(true);
    } else {
      setFallbackError(true);
    }
  };

  // Si les deux images échouent, afficher un placeholder stylisé
  if (fallbackError) {
    return (
      <div
        className={`relative overflow-hidden rounded-lg bg-gradient-to-br from-cyan-400 to-blue-600 flex items-center justify-center ${className}`}
        style={{ width: `${width}px`, height: `${height}px` }}
      >
        <div className='text-center text-white p-4'>
          <div className='text-4xl mb-2'>🏄‍♂️</div>
          <div className='text-sm font-medium'>{fallbackText}</div>
          <div className='text-xs opacity-75'>Dakhla, Morocco</div>
        </div>
      </div>
    );
  }

  return (
    <Image
      src={imageError ? fallbackSrc : src}
      alt={alt}
      width={width}
      height={height}
      className={className}
      priority={priority}
      onError={handleError}
      style={{
        objectFit: 'cover',
        width: '100%',
        height: 'auto',
      }}
    />
  );
}
","usedDeprecatedRules":[]},{"filePath":"/Users/pro/Windventurefinal/src/components/SimpleMotion.tsx","messages":[{"ruleId":"@typescript-eslint/no-unused-vars","message":"Definition for rule '@typescript-eslint/no-unused-vars' was not found.","line":1,"column":1,"endLine":1,"endColumn":2,"severity":2,"nodeType":null},{"ruleId":"@typescript-eslint/no-explicit-any","message":"Definition for rule '@typescript-eslint/no-explicit-any' was not found.","line":1,"column":1,"endLine":1,"endColumn":2,"severity":2,"nodeType":null}],"suppressedMessages":[],"errorCount":2,"fatalErrorCount":0,"warningCount":0,"fixableErrorCount":0,"fixableWarningCount":0,"source":"'use client';

import React from 'react';

interface SimpleMotionProps {
  children: React.ReactNode;
  className?: string;
  style?: React.CSSProperties;
  initial?: any;
  animate?: any;
  transition?: any;
  whileInView?: any;
  delay?: number;
  duration?: number;
}

export function SimpleMotion({
  children,
  className = '',
  style = {},
  initial,
  animate,
  transition,
  whileInView,
  delay = 0,
  duration = 0.6,
}: SimpleMotionProps) {
  // Convert framer-motion props to CSS animations
  let animationClass = '';

  if (initial?.opacity === 0 && animate?.opacity === 1) {
    if (initial?.y && animate?.y === 0) {
      animationClass = 'animate-fade-in-up';
    } else if (initial?.x && animate?.x === 0) {
      if (initial.x < 0) {
        animationClass = 'animate-fade-in-left';
      } else {
        animationClass = 'animate-fade-in-right';
      }
    } else {
      animationClass = 'animate-fade-in-up';
    }
  } else if (initial?.scale && animate?.scale === 1) {
    animationClass = 'animate-scale-in';
  }

  const animationStyle = {
    ...style,
    animationDelay: `${delay}s`,
    animationDuration: `${duration}s`,
  };

  return (
    <div className={`${animationClass} ${className}`} style={animationStyle}>
      {children}
    </div>
  );
}

// Simple AnimatePresence replacement
export function SimpleAnimatePresence({
  children,
}: {
  children: React.ReactNode;
}) {
  return <>{children}</>;
}

// Export as motion for compatibility
export const motion = SimpleMotion;
export const AnimatePresence = SimpleAnimatePresence;
